import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Camera, Upload, AlertTriangle, CheckCircle, Wifi, WifiOff, MapPin, Loader, TrendingUp, Cpu, Users, RefreshCw } from 'lucide-react';

// --- CONFIGURATION ---
// IMPORTANT: This URL MUST MATCH the port your Node.js server runs on.
const API_BASE_URL = 'http://localhost:5000'; 
const LOCAL_STORAGE_KEY = 'sih_offline_uploads';

// --- Utility Functions ---

// 1. Mock Local Storage & Offline Status (Simulates AsyncStorage on mobile)
const getLocalUploads = () => {
  const data = localStorage.getItem(LOCAL_STORAGE_KEY);
  return data ? JSON.parse(data) : [];
};

const saveLocalUploads = (uploads) => {
  localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(uploads));
};

const generateMockCaseId = () => `CASE-${Math.floor(Math.random() * 90000) + 10000}`;

// Helper hook for network status simulation
const useOnlineStatus = () => {
  const [isOnline, setIsOnline] = useState(true);

  useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  return isOnline;
};

// --- Component: Beneficiary (Loan Recipient) App ---

const BeneficiaryApp = ({ onSyncComplete }) => {
  const [capturedImage, setCapturedImage] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [message, setMessage] = useState('');
  const [localUploads, setLocalUploads] = useState(getLocalUploads());
  const [isOfflineMode, setIsOfflineMode] = useState(false); // Simulates network toggle

  const isOnline = useOnlineStatus() && !isOfflineMode;
  const SyncIcon = isOnline ? Wifi : WifiOff;

  // Function to simulate image capture (uses a placeholder)
  const handleCaptureImage = () => {
    setMessage('');
    // Use a unique placeholder image for realism
    setCapturedImage(`https://placehold.co/400x300/e0f2f1/004d40?text=Asset+Photo+${Date.now()}`);
  };

  // Function to simulate saving the data locally (Offline-First Logic)
  const handleSaveLocally = useCallback(() => {
    if (!capturedImage) {
      setMessage({ type: 'error', text: 'Please capture an asset image first.' });
      return;
    }

    setIsProcessing(true);
    const newUpload = {
      id: generateMockCaseId(),
      assetImage: capturedImage,
      beneficiary: 'Beneficiary ID: AB001', 
      timestamp: new Date().toISOString(),
      gps: '12.9716¬∞ N, 77.5946¬∞ E (Mocked)',
      status: 'Awaiting Sync',
      isSynced: false,
    };

    const updatedUploads = [...localUploads, newUpload];
    saveLocalUploads(updatedUploads);
    setLocalUploads(updatedUploads);
    setCapturedImage(null);

    setTimeout(() => {
      setIsProcessing(false);
      setMessage({ type: 'success', text: `‚úÖ Upload saved locally! Case ID: ${newUpload.id}. Will sync when online.` });
    }, 1000);
  }, [capturedImage, localUploads]);

  // Function to synchronize data to the backend
  const handleSyncToBackend = useCallback(async () => {
    if (localUploads.length === 0) {
      setMessage({ type: 'info', text: 'No pending uploads to sync.' });
      return;
    }
    if (!isOnline) {
      setMessage({ type: 'error', text: 'üö® Cannot sync: Device is currently offline.' });
      return;
    }

    setIsProcessing(true);
    setMessage({ type: 'info', text: `Starting sync of ${localUploads.length} case(s) to the cloud...` });

    try {
        // Send the local uploads to the Node.js backend (Port 5000)
        const response = await fetch(`${API_BASE_URL}/api/upload`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ cases: localUploads }),
        });

        if (!response.ok) {
            throw new Error(`Server error: ${response.status}`);
        }
        
        // Success! Clear local storage and update app state
        saveLocalUploads([]);
        setLocalUploads([]);
        
        const result = await response.json();
        setMessage({ type: 'success', text: `üöÄ Sync Complete! ${result.message}` });
        
        onSyncComplete(); // Trigger officer dashboard refresh

    } catch (error) {
        console.error("Sync Error:", error);
        setMessage({ type: 'error', text: `Sync failed! Is the backend running at ${API_BASE_URL}?` });
    } finally {
        setIsProcessing(false);
    }
  }, [localUploads, isOnline, onSyncComplete]);


  useEffect(() => {
    if (isOnline && localUploads.length > 0) {
        setMessage({ type: 'warning', text: `üåê Network restored. ${localUploads.length} pending case(s) ready to sync.` });
    }
  }, [isOnline, localUploads.length]);

  return (
    <div className="bg-white p-6 rounded-xl shadow-lg h-full flex flex-col">
      <div className="flex justify-between items-center mb-4 border-b pb-3">
        <h2 className="text-2xl font-bold text-teal-600 flex items-center">
          <Upload className="w-6 h-6 mr-2"/> Beneficiary App
        </h2>
        <button
          onClick={() => setIsOfflineMode(prev => !prev)}
          className={`px-3 py-1 rounded-full text-sm font-medium transition duration-300 flex items-center ${isOfflineMode ? 'bg-red-500 text-white shadow-md' : 'bg-green-100 text-green-700 hover:bg-green-200'}`}
          title="Simulate network connectivity"
        >
          <SyncIcon className="w-4 h-4 mr-1" />
          {isOfflineMode ? 'Forced Offline' : 'Online Mode'}
        </button>
      </div>

      <div className="flex-grow overflow-y-auto">
        {/* 1. Asset Capture */}
        <div className="mb-6 p-4 border border-gray-200 rounded-lg">
          <p className="text-lg font-semibold mb-3">Step 1: Capture Loan Asset Proof</p>
          {capturedImage ? (
            <div className="border-4 border-teal-400 p-2 rounded-lg bg-gray-50 mb-4">
              <img src={capturedImage} alt="Captured Asset" className="w-full h-auto rounded-lg" />
            </div>
          ) : (
            <div className="h-32 bg-gray-100 border-2 border-dashed border-gray-400 rounded-lg flex items-center justify-center mb-4">
              <Camera className="w-8 h-8 text-gray-500" />
              <p className="ml-2 text-gray-600">No Asset Captured Yet</p>
            </div>
          )}

          <button
            onClick={handleCaptureImage}
            className="w-full bg-teal-500 text-white py-2 rounded-lg hover:bg-teal-600 transition duration-300 shadow-md flex items-center justify-center"
          >
            <Camera className="w-5 h-5 mr-2"/> Capture Asset Image/Video
          </button>
        </div>

        {/* 2. Upload Status & Actions */}
        <div className="p-4 border border-gray-200 rounded-lg">
          <p className="text-lg font-semibold mb-3">Step 2: Submit & Sync</p>
          <button
            onClick={handleSaveLocally}
            disabled={isProcessing || !capturedImage}
            className={`w-full py-2 rounded-lg mb-3 transition duration-300 shadow-md ${
              isProcessing || !capturedImage
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                : 'bg-indigo-500 text-white hover:bg-indigo-600'
            } flex items-center justify-center`}
          >
            {isProcessing ? <Loader className="w-5 h-5 mr-2 animate-spin"/> : <MapPin className="w-5 h-5 mr-2"/>}
            Save Proof Locally (with GPS Tag)
          </button>

          <button
            onClick={handleSyncToBackend}
            disabled={isProcessing || localUploads.length === 0}
            className={`w-full py-2 rounded-lg transition duration-300 shadow-md ${
              isProcessing || localUploads.length === 0
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                : isOnline
                  ? 'bg-green-500 text-white hover:bg-green-600'
                  : 'bg-yellow-500 text-white hover:bg-yellow-600'
            } flex items-center justify-center`}
          >
            {isProcessing ? <Loader className="w-5 h-5 mr-2 animate-spin"/> : <Wifi className="w-5 h-5 mr-2"/>}
            {isOnline ? `Sync All ${localUploads.length} Pending Case(s)` : `Sync PENDING (${localUploads.length}) - OFFLINE`}
          </button>
        </div>

        {/* Status Message */}
        <div className="mt-6 p-4 rounded-lg bg-blue-50 border border-blue-200">
          <p className="font-semibold text-blue-700">Status:</p>
          {message.text && (
            <p className={`mt-1 text-sm font-medium ${message.type === 'success' ? 'text-green-600' : message.type === 'error' ? 'text-red-600' : 'text-blue-600'}`}>
              {message.text}
            </p>
          )}
          {localUploads.length > 0 && (
            <p className="text-sm mt-2 text-indigo-700">
              **Pending Local Uploads: {localUploads.length}**
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

// --- Component: Officer Review Dashboard ---

const OfficerDashboard = ({ fetchTrigger }) => {
  const [cases, setCases] = useState([]);
  const [selectedCase, setSelectedCase] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // Function to fetch case data from the backend
  const fetchCases = useCallback(async () => {
    setIsLoading(true);
    try {
        // Fetch current cases from the Node.js backend (Port 5000)
        const response = await fetch(`${API_BASE_URL}/api/cases`);
        if (!response.ok) throw new Error('Failed to fetch cases from backend.');
        
        const data = await response.json();
        setCases(data);
    } catch (error) {
        console.error("Fetch Error: Ensure backend is running.", error);
    } finally {
        setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    // Initial fetch when component loads or sync is complete
    fetchCases();
    
    // Set up a refresh interval to simulate real-time updates for the demo
    const interval = setInterval(fetchCases, 5000); 
    return () => clearInterval(interval);
  }, [fetchCases, fetchTrigger]); // fetchTrigger ensures immediate update after sync

  // Function to handle officer review action
  const handleReviewAction = useCallback(async (caseId, action) => {
    try {
        // Send review action to the Node.js backend (Port 5000)
        const response = await fetch(`${API_BASE_URL}/api/review`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ case_id: caseId, action }),
        });
        
        if (!response.ok) throw new Error('Failed to submit review action.');

        // Refresh the case list and close the detail view after a successful action
        fetchCases();
        setSelectedCase(null);

    } catch (error) {
        console.error("Review Action Error:", error);
    }
  }, [fetchCases]);
  
  // Use useMemo for visual consistency
  const statusColors = useMemo(() => ({
    'Suspicious': 'text-red-600 bg-red-100 border-red-300',
    'Verified': 'text-green-600 bg-green-100 border-green-300',
    'Awaiting AI Check': 'text-yellow-600 bg-yellow-100 border-yellow-300',
    'Approved': 'text-blue-600 bg-blue-100 border-blue-300',
    'Rejected': 'text-gray-600 bg-gray-100 border-gray-300',
    'Processing': 'text-purple-600 bg-purple-100 border-purple-300',
  }), []);

  const getStatusTag = (status) => (
    <span className={`px-2 py-1 rounded-full text-xs font-semibold border ${statusColors[status] || statusColors['Processing']}`}>
      {status}
    </span>
  );

  return (
    <div className="bg-gray-50 p-6 rounded-xl shadow-lg h-full flex flex-col">
      <h2 className="text-2xl font-bold text-gray-800 border-b pb-3 mb-4 flex items-center">
        <Cpu className="w-6 h-6 mr-2 text-red-500"/> Officer Review Dashboard
        <button onClick={fetchCases} disabled={isLoading} className="ml-4 p-1 rounded-full text-gray-500 hover:text-indigo-600 transition">
             {isLoading ? <Loader className="w-5 h-5 animate-spin"/> : <RefreshCw className="w-5 h-5"/>}
        </button>
      </h2>

      <div className="flex flex-grow overflow-hidden">
        {/* Case List (Left Pane) */}
        <div className="w-1/3 pr-4 border-r overflow-y-auto">
          <h3 className="text-xl font-semibold mb-3 text-gray-700">Verification Cases ({cases.length})</h3>
          <p className='text-sm text-red-500 mb-4 font-medium flex items-center'><AlertTriangle className='w-4 h-4 mr-1'/> Critical: {cases.filter(c => c.ai_status === 'Suspicious' && !c.reviewStatus).length} Pending Cases</p>
          <div className="space-y-3">
            {cases.map(c => (
              <div
                key={c.id}
                onClick={() => setSelectedCase(c)}
                className={`p-3 rounded-lg border cursor-pointer transition duration-300 ${
                  selectedCase && selectedCase.id === c.id ? 'border-teal-500 bg-teal-50 shadow-md' : 'bg-white hover:bg-gray-100'
                }`}
              >
                <p className="font-bold text-sm text-gray-800">{c.id}</p>
                <p className="text-xs text-gray-500 mb-1">Beneficiary: {c.beneficiary}</p>
                <div className="flex justify-between items-center">
                    {getStatusTag(c.ai_status)}
                    {c.reviewStatus && getStatusTag(c.reviewStatus)}
                </div>
              </div>
            ))}
            {cases.length === 0 && !isLoading && <p className="text-gray-500 text-center mt-8">No cases pending review.</p>}
          </div>
        </div>

        {/* Case Detail (Right Pane) */}
        <div className="w-2/3 pl-4 overflow-y-auto">
          {selectedCase ? (
            <div className="space-y-4">
              <h3 className="text-2xl font-bold text-gray-800">{selectedCase.id}</h3>
              <div className="p-4 rounded-lg bg-white shadow-inner">
                <p className="font-semibold text-lg mb-2">AI Verification Report:</p>
                <div className={`p-4 rounded-lg font-bold text-xl flex items-center ${
                    selectedCase.ai_status === 'Suspicious'
                      ? 'bg-red-500 text-white shadow-xl'
                      : selectedCase.ai_status === 'Verified'
                        ? 'bg-green-500 text-white shadow-xl'
                        : 'bg-yellow-500 text-gray-800 shadow-xl'
                  }`}>
                  {selectedCase.ai_status === 'Suspicious' ? <AlertTriangle className="w-6 h-6 mr-2"/> : <CheckCircle className="w-6 h-6 mr-2"/>}
                  AI Status: {selectedCase.ai_status}
                </div>
                <p className="text-sm mt-2 text-gray-700">Confidence Score: {selectedCase.confidence}</p>
                {selectedCase.ai_status === 'Suspicious' && <p className="text-red-300 italic mt-1">Reason: {selectedCase.ai_reason}</p>}
              </div>

              <div className="space-y-3">
                <p className="font-semibold text-lg border-b pb-2">Loan Asset Evidence:</p>
                <img src={selectedCase.assetImage} alt="Asset Proof" className="w-full h-auto rounded-lg shadow-md" />
                <div className="flex items-center text-sm text-gray-600">
                  <MapPin className="w-4 h-4 mr-1"/>
                  **GeoTag:** {selectedCase.gps}
                </div>
                <div className="text-sm text-gray-600">
                  **Upload Date:** {new Date(selectedCase.timestamp).toLocaleString()}
                </div>
                {selectedCase.officer && <p className="text-sm font-semibold text-blue-600 mt-2">Reviewed By: {selectedCase.officer}</p>}
              </div>

              {/* Review Actions */}
              {!selectedCase.reviewStatus && (
                <div className="pt-4 border-t mt-4 flex space-x-4">
                  <button
                    onClick={() => handleReviewAction(selectedCase.id, 'Approved')}
                    className="flex-1 bg-green-600 text-white py-3 rounded-lg hover:bg-green-700 font-bold transition duration-300 shadow-md flex items-center justify-center"
                  >
                    <CheckCircle className="w-5 h-5 mr-2"/> Approve Loan Use
                  </button>
                  <button
                    onClick={() => handleReviewAction(selectedCase.id, 'Rejected')}
                    className="flex-1 bg-red-600 text-white py-3 rounded-lg hover:bg-red-700 font-bold transition duration-300 shadow-md flex items-center justify-center"
                  >
                    <AlertTriangle className="w-5 h-5 mr-2"/> Reject/Flag for Field Visit
                  </button>
                </div>
              )}
              {selectedCase.reviewStatus && (
                 <div className="p-4 bg-gray-100 rounded-lg text-center font-bold text-lg text-gray-600">
                    Review {selectedCase.reviewStatus}: No further action needed.
                 </div>
              )}
            </div>
          ) : (
            <div className="text-center mt-20 p-8 bg-white rounded-lg border-2 border-dashed border-gray-300">
              <TrendingUp className="w-12 h-12 mx-auto text-teal-400 mb-3"/>
              <p className="text-gray-500 text-xl font-medium">Select a case from the left to begin remote verification and review the AI report.</p>
              {isLoading && <Loader className="w-8 h-8 mx-auto mt-4 animate-spin text-indigo-500"/>}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

