import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Camera, Upload, AlertTriangle, CheckCircle, Wifi, WifiOff, MapPin, Loader, TrendingUp, Cpu, Users, RefreshCw } from 'lucide-react';

// --- CONFIGURATION ---
const API_BASE_URL = 'http://localhost:5000'; 
const LOCAL_STORAGE_KEY = 'sih_offline_uploads';

// --- Utility Functions ---

const getLocalUploads = () => {
  const data = localStorage.getItem(LOCAL_STORAGE_KEY);
  return data ? JSON.parse(data) : [];
};

const saveLocalUploads = (uploads) => {
  localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(uploads));
};

const generateMockCaseId = () => `CASE-${Math.floor(Math.random() * 90000) + 10000}`;

const useOnlineStatus = () => {
  const [isOnline, setIsOnline] = useState(true);

  useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  return isOnline;
};

// --- Component: Beneficiary (Loan Recipient) App ---

const BeneficiaryApp = ({ onSyncComplete }) => {
  const [capturedImage, setCapturedImage] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [message, setMessage] = useState('');
  const [localUploads, setLocalUploads] = useState(getLocalUploads());
  const [isOfflineMode, setIsOfflineMode] = useState(false); 

  const isOnline = useOnlineStatus() && !isOfflineMode;
  const SyncIcon = isOnline ? Wifi : WifiOff;

  const handleCaptureImage = () => {
    setMessage('');
    // Updated placeholder color to match the teal theme
    setCapturedImage(`https://placehold.co/400x300/14b8a6/FFFFFF?text=Asset+Photo+${Date.now()}`);
  };

  const handleSaveLocally = useCallback(() => {
    if (!capturedImage) {
      setMessage({ type: 'error', text: 'Please capture an asset image first.' });
      return;
    }

    setIsProcessing(true);
    const newUpload = {
      id: generateMockCaseId(),
      assetImage: capturedImage,
      beneficiary: 'Beneficiary ID: AB001', 
      timestamp: new Date().toISOString(),
      gps: '12.9716¬∞ N, 77.5946¬∞ E (Mocked)',
      status: 'Awaiting Sync',
      isSynced: false,
    };

    const updatedUploads = [...localUploads, newUpload];
    saveLocalUploads(updatedUploads);
    setLocalUploads(updatedUploads);
    setCapturedImage(null);

    setTimeout(() => {
      setIsProcessing(false);
      setMessage({ type: 'success', text: `‚úÖ Proof saved locally! Case ID: ${newUpload.id}.` });
    }, 1000);
  }, [capturedImage, localUploads]);

  const handleSyncToBackend = useCallback(async () => {
    if (localUploads.length === 0) {
      setMessage({ type: 'info', text: 'No pending uploads to sync.' });
      return;
    }
    if (!isOnline) {
      setMessage({ type: 'error', text: 'üö® Cannot sync: Device is currently offline.' });
      return;
    }

    setIsProcessing(true);
    setMessage({ type: 'info', text: `Starting sync of ${localUploads.length} case(s) to the cloud...` });

    try {
        const response = await fetch(`${API_BASE_URL}/api/upload`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ cases: localUploads }),
        });

        if (!response.ok) {
            throw new Error(`Server error: ${response.status}`);
        }
        
        saveLocalUploads([]);
        setLocalUploads([]);
        
        const result = await response.json();
        setMessage({ type: 'success', text: `üöÄ Sync Complete! ${result.message}` });
        
        onSyncComplete(); 

    } catch (error) {
        console.error("Sync Error:", error);
        setMessage({ type: 'error', text: `Sync failed! Is the backend running at ${API_BASE_URL}?` });
    } finally {
        setIsProcessing(false);
    }
  }, [localUploads, isOnline, onSyncComplete]);


  useEffect(() => {
    if (isOnline && localUploads.length > 0) {
        setMessage({ type: 'warning', text: `üåê Network restored. ${localUploads.length} pending case(s) ready to sync.` });
    }
  }, [isOnline, localUploads.length]);

  return (
    <div className="flex justify-center items-start h-full p-4">
      {/* Phone Frame */}
      <div className="mobile-frame bg-gray-900 shadow-2xl rounded-[3rem] p-1 w-[380px] h-[650px] relative">
        {/* Inner Screen */}
        <div className="bg-white rounded-[2.5rem] h-full overflow-hidden flex flex-col">
          
          {/* Status Bar / Header (VIBRANT) */}
          <div className="p-3 bg-indigo-700 text-white flex justify-between items-center shadow-lg">
            <h3 className="font-bold text-lg">‡§ß‡§® ‡§∏‡§æ‡§ï‡•ç‡§∑‡•Ä (Dhan Sakshi)</h3>
            <div className="flex items-center space-x-2">
                <span className="text-sm">{new Date().toLocaleTimeString('en-US', {hour: '2-digit', minute:'2-digit'})}</span>
                <SyncIcon className="w-5 h-5 text-teal-300"/>
                <span className={`text-xs font-semibold px-2 rounded ${isOfflineMode ? 'bg-red-500' : 'bg-green-500'}`}>{isOfflineMode ? 'OFF' : 'ON'}</span>
            </div>
          </div>
          
          <div className="flex-grow overflow-y-auto p-4 space-y-4">
            
            {/* 1. Asset Capture */}
            <div className="p-4 border border-gray-200 rounded-xl bg-gray-50 shadow-sm">
              <p className="font-semibold text-gray-700 mb-3 flex items-center"><Camera className='w-4 h-4 mr-2 text-teal-600'/> 1. Capture Loan Asset Proof</p>
              {capturedImage ? (
                <div className="border-4 border-teal-600 p-1 rounded-lg bg-gray-200 mb-3">
                  <img src={capturedImage} alt="Captured Asset" className="w-full h-auto rounded-md" />
                </div>
              ) : (
                <div className="h-28 bg-gray-100 border-2 border-dashed border-gray-400 rounded-lg flex items-center justify-center mb-3">
                  <Camera className="w-8 h-8 text-gray-500" />
                  <p className="ml-2 text-gray-600 text-sm">Tap to Capture...</p>
                </div>
              )}

              <button
                onClick={handleCaptureImage}
                className="w-full bg-teal-600 text-white py-2 rounded-lg hover:bg-teal-700 transition duration-300 shadow-lg flex items-center justify-center text-sm font-medium"
              >
                <Camera className="w-4 h-4 mr-2"/> Capture Proof
              </button>
            </div>

            {/* 2. Upload Status & Actions */}
            <div className="p-4 border border-gray-200 rounded-xl bg-white shadow-sm">
              <p className="font-semibold text-gray-700 mb-3 flex items-center"><Upload className='w-4 h-4 mr-2 text-indigo-600'/> 2. Submit & Synchronize</p>
              
              {/* Toggle Switch */}
              <div className="mb-3 flex justify-between items-center p-2 rounded-lg bg-gray-100 border border-gray-200">
                <span className="text-sm font-medium text-gray-700">Simulate Offline Mode:</span>
                <button
                  onClick={() => setIsOfflineMode(prev => !prev)}
                  className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none ${isOfflineMode ? 'bg-red-500' : 'bg-green-500'}`}
                  aria-checked={isOfflineMode}
                  role="switch"
                >
                  <span className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${isOfflineMode ? 'translate-x-6' : 'translate-x-1'}`}></span>
                </button>
              </div>

              <button
                onClick={handleSaveLocally}
                disabled={isProcessing || !capturedImage}
                className={`w-full py-2 rounded-lg mb-3 transition duration-300 shadow-lg text-sm font-medium ${
                  isProcessing || !capturedImage
                    ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                    : 'bg-indigo-600 text-white hover:bg-indigo-700 shadow-xl'
                } flex items-center justify-center`}
              >
                {isProcessing ? <Loader className="w-4 h-4 mr-2 animate-spin"/> : <MapPin className="w-4 h-4 mr-2"/>}
                Save Proof Locally (Offline)
              </button>

              <button
                onClick={handleSyncToBackend}
                disabled={isProcessing || localUploads.length === 0 || !isOnline}
                className={`w-full py-2 rounded-lg transition duration-300 shadow-md text-sm font-medium ${
                  isProcessing || localUploads.length === 0
                    ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                    : isOnline
                      ? 'bg-green-600 text-white hover:bg-green-700 shadow-xl'
                      : 'bg-yellow-500 text-gray-800 cursor-not-allowed'
                } flex items-center justify-center`}
              >
                {isProcessing ? <Loader className="w-4 h-4 mr-2 animate-spin"/> : <Wifi className="w-4 h-4 mr-2"/>}
                {isOnline ? `Sync All ${localUploads.length} Pending Case(s)` : `OFFLINE - ${localUploads.length} PENDING`}
              </button>
            </div>

            {/* Status Message */}
            <div className="mt-4 p-3 rounded-xl bg-blue-50 border border-blue-200 text-sm">
              <p className="font-semibold text-blue-800">Status Updates:</p>
              {message.text && (
                <p className={`mt-1 font-medium ${message.type === 'success' ? 'text-green-600' : message.type === 'error' ? 'text-red-600' : 'text-blue-600'}`}>
                  {message.text}
                </p>
              )}
              {localUploads.length > 0 && (
                <p className="font-bold mt-2 text-indigo-700">
                  **Pending Local Uploads: {localUploads.length}**
                </p>
              )}
            </div>

          </div>
          
        </div>
      </div>
    </div>
  );
};

// --- Component: Officer Review Dashboard ---

const OfficerDashboard = ({ fetchTrigger }) => {
  const [cases, setCases] = useState([]);
  const [selectedCase, setSelectedCase] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const fetchCases = useCallback(async () => {
    setIsLoading(true);
    try {
        const response = await fetch(`${API_BASE_URL}/api/cases`);
        if (!response.ok) throw new Error('Failed to fetch cases from backend.');
        
        const data = await response.json();
        setCases(data);
    } catch (error) {
        console.error("Fetch Error: Ensure backend is running.", error);
    } finally {
        setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchCases();
    const interval = setInterval(fetchCases, 5000); 
    return () => clearInterval(interval);
  }, [fetchCases, fetchTrigger]); 

  const handleReviewAction = useCallback(async (caseId, action) => {
    try {
        const response = await fetch(`${API_BASE_URL}/api/review`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ case_id: caseId, action }),
        });
        
        if (!response.ok) throw new Error('Failed to submit review action.');

        fetchCases();
        setSelectedCase(null);

    } catch (error) {
        console.error("Review Action Error:", error);
    }
  }, [fetchCases]);
  
  const statusColors = useMemo(() => ({
    'Suspicious': 'text-red-600 bg-red-100 border-red-300',
    'Verified': 'text-green-600 bg-green-100 border-green-300',
    'Awaiting AI Check': 'text-yellow-600 bg-yellow-100 border-yellow-300',
    'Approved': 'text-indigo-600 bg-indigo-100 border-indigo-300', // Changed to Indigo for final approval
    'Rejected': 'text-gray-600 bg-gray-100 border-gray-300',
    'Processing': 'text-purple-600 bg-purple-100 border-purple-300',
  }), []);

  const getStatusTag = (status) => (
    <span className={`px-2 py-1 rounded-full text-xs font-semibold border ${statusColors[status] || statusColors['Processing']}`}>
      {status}
    </span>
  );

  return (
    <div className="bg-white p-6 rounded-xl h-full flex flex-col">
      <h2 className="text-2xl font-bold text-gray-800 border-b pb-3 mb-4 flex items-center">
        <Cpu className="w-6 h-6 mr-2 text-red-500"/> Remote Verification Dashboard
        <button onClick={fetchCases} disabled={isLoading} className="ml-4 p-1 rounded-full text-gray-500 hover:text-indigo-600 transition">
             {isLoading ? <Loader className="w-5 h-5 animate-spin"/> : <RefreshCw className="w-5 h-5"/>}
        </button>
      </h2>

      <div className="flex flex-grow overflow-hidden">
        {/* Case List (Left Pane) */}
        <div className="w-1/3 pr-4 border-r overflow-y-auto custom-scrollbar">
          <h3 className="text-xl font-semibold mb-3 text-gray-700">Verification Cases ({cases.length})</h3>
          <p className='text-sm text-red-600 mb-4 font-bold flex items-center bg-red-50 p-2 rounded-lg border border-red-200'><AlertTriangle className='w-4 h-4 mr-1'/> HIGH PRIORITY: {cases.filter(c => c.ai_status === 'Suspicious' && !c.reviewStatus).length} Cases</p>
          <div className="space-y-3">
            {cases.map(c => (
              <div
                key={c.id}
                onClick={() => setSelectedCase(c)}
                className={`p-3 rounded-xl border cursor-pointer transition duration-300 case-card ${
                  selectedCase && selectedCase.id === c.id ? 'border-indigo-600 bg-indigo-50 shadow-lg' : 'bg-gray-50 hover:bg-gray-100'
                } ${c.ai_status === 'Suspicious' && !c.reviewStatus ? 'border-red-400 bg-red-100' : ''}`}
              >
                <p className="font-bold text-sm text-gray-800">{c.id}</p>
                <p className="text-xs text-gray-500 mb-1">Beneficiary: {c.beneficiary}</p>
                <div className="flex justify-between items-center mt-2">
                    {getStatusTag(c.ai_status)}
                    {c.reviewStatus && getStatusTag(c.reviewStatus)}
                </div>
              </div>
            ))}
            {cases.length === 0 && !isLoading && <p className="text-gray-500 text-center mt-8">No cases pending review.</p>}
          </div>
        </div>

        {/* Case Detail (Right Pane) */}
        <div className="w-2/3 pl-4 overflow-y-auto custom-scrollbar">
          {selectedCase ? (
            <div className="space-y-4">
              <h3 className="text-2xl font-bold text-gray-800">{selectedCase.id}</h3>
              <div className="p-5 rounded-xl bg-white shadow-xl border-t-4 border-red-500">
                <p className="font-semibold text-xl mb-2 flex items-center"><Cpu className='w-5 h-5 mr-2 text-red-500'/> AI Verification Report:</p>
                <div className={`p-4 rounded-lg font-bold text-xl flex items-center transition duration-300 ${
                    selectedCase.ai_status === 'Suspicious'
                      ? 'bg-red-500 text-white shadow-lg pulse-bg'
                      : 'bg-green-500 text-white shadow-lg'
                  }`}>
                  {selectedCase.ai_status === 'Suspicious' ? <AlertTriangle className="w-6 h-6 mr-2"/> : <CheckCircle className="w-6 h-6 mr-2"/>}
                  AI Status: {selectedCase.ai_status}
                </div>
                <p className="text-sm mt-2 text-gray-700 font-medium">Confidence Score: **{selectedCase.confidence}**</p>
                <p className="text-sm italic mt-1 text-gray-500">Analysis: {selectedCase.ai_reason}</p>
              </div>

              <div className="space-y-4 p-5 bg-gray-100 rounded-xl shadow-inner">
                <p className="font-semibold text-lg border-b pb-2 text-gray-700">Loan Asset Evidence:</p>
                <img src={selectedCase.assetImage} alt="Asset Proof" className="w-full h-auto rounded-lg shadow-md border-2 border-gray-300" />
                <div className="flex items-center text-sm text-gray-600">
                  <MapPin className="w-4 h-4 mr-1 text-teal-600"/>
                  **GeoTag:** {selectedCase.gps}
                </div>
                <div className="text-sm text-gray-600">
                  **Upload Date:** {new Date(selectedCase.timestamp).toLocaleString()}
                </div>
                {selectedCase.officer && <p className="text-sm font-bold text-indigo-700 mt-2">Reviewed By: {selectedCase.officer}</p>}
              </div>

              {/* Review Actions */}
              {!selectedCase.reviewStatus && (
                <div className="pt-4 border-t mt-4 flex space-x-4">
                  <button
                    onClick={() => handleReviewAction(selectedCase.id, 'Approved')}
                    className="flex-1 bg-indigo-600 text-white py-3 rounded-xl hover:bg-indigo-700 font-bold transition duration-300 shadow-xl flex items-center justify-center text-lg"
                  >
                    <CheckCircle className="w-5 h-5 mr-2"/> Approve Loan Use
                  </button>
                  <button
                    onClick={() => handleReviewAction(selectedCase.id, 'Rejected')}
                    className="flex-1 bg-red-600 text-white py-3 rounded-xl hover:bg-red-700 font-bold transition duration-300 shadow-xl flex items-center justify-center text-lg"
                  >
                    <AlertTriangle className="w-5 h-5 mr-2"/> Flag for Field Visit
                  </button>
                </div>
              )}
              {selectedCase.reviewStatus && (
                 <div className="p-4 bg-indigo-100 rounded-xl text-center font-bold text-lg text-indigo-700 border-2 border-indigo-400">
                    Review {selectedCase.reviewStatus} by Officer.
                 </div>
              )}
            </div>
          ) : (
            <div className="text-center mt-20 p-8 bg-white rounded-xl border-2 border-dashed border-gray-300 shadow-inner">
              <TrendingUp className="w-12 h-12 mx-auto text-teal-500 mb-3"/>
              <p className="text-gray-600 text-xl font-medium">Select a case to begin remote verification.</p>
              {isLoading && <Loader className="w-8 h-8 mx-auto mt-4 animate-spin text-indigo-600"/>}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

